import React from 'react';

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = { value: '' };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = (event) => {
        const s = event.target.value;
        let romanMap = new Map();
        romanMap.set("I", 1);
        romanMap.set("V", 5);
        romanMap.set("X", 10);
        romanMap.set("L", 50);
        romanMap.set("C", 100);
        romanMap.set("D", 500);
        romanMap.set("M", 1000);

        let romanValue = 0;
        let i = s.length - 1;

        if (s.length >= 1 && s.length <= 15) {
            while (i >= 0) {
                let rVal = 0;
                let rPresent = false;
                if (s[i] === 'V' && s[i - 1] === 'I') {
                    rVal = 4;
                    rPresent = true;
                    i--;
                } else if (s[i] === 'X' && s[i - 1] === 'I') {
                    rVal = 9;
                    rPresent = true;
                    i--;
                } else if (s[i - 1] === 'X' && s[i] === 'L') {
                    rVal = 40;
                    rPresent = true;
                    i--;
                } else if (s[i - 1] === 'X' && s[i] === 'C') {
                    rVal = 90;
                    rPresent = true;
                    i--;
                } else if (s[i - 1] === 'C' && s[i] === 'D') {
                    rVal = 400;
                    rPresent = true;
                    i--;
                } else if (s[i - 1] === 'C' && s[i] === 'M') {
                    rVal = 900;
                    rPresent = true;
                    i--;
                } else {
                    rVal = romanMap.get(s[i]);
                }
                romanValue = romanValue + rVal;
                i--;
            }
        }

        this.setState({ value: romanValue });
    }

    render() {
        return (
            <div className='main'>
                <input type="text" id="roman" name="roman" className="roman"
                    value={this.state.value} onChange={this.handleChange} /><br /><br />
                <span>{this.state.value}</span>
            </div>
        );
    }
}

export default App;
